##### Checke for file existance #####
if [ `$PS ; echo $?` -ne 0 ]; then
	printf '\033[36mError: \033[m check the relative path for push_swap\n'
	exit
elif [ `$CH ; echo $?` -ne 0 ]; then
	printf '\033[36mError: \033[m check the relative path for checker\n'
	exit
fi

##### Check each arg #####
FAIL=0

for arg in "${ARGS[@]}"
do
	if [ "`$PS $arg | $CH $arg`" != "OK" ]; then
		printf '\033[31m[KO]\033[m Error Occured\n'
	else
		len=`$PS $arg | wc -l`
		if [ "$(echo "$len < 700" | bc)" -eq 1 ]; then
			printf '\033[32m[OK]\033[m '
		else
			printf '\033[31m[KO]\033[m '
			FAIL=1
		fi
		echo $len
	fi
done

##### Display the final result #####
if [ $FAIL == 0 ]; then
	printf '\033[32m:) yay!\033[m\n'
else
	printf '\033[32m:( oops!\033[m\n'
fi
